mapper.select:
select score
from answer_ratings
where answer_id = #{answerId}
  and user_id = #{userId};

mapper.update:
update answers
set total_score = total_score - #{oldScore} + #{newScore}
where id = #{answerId}

update answer_ratings
set score = #{score}
where answer_id = #{answerId}
  and user_id = #{userId};
