mapper.update:
update posts
set deleted_at = current_timestamp
where id = #{codeReviewId};

mapper.select:
SELECT p.id id, p.title title, p.anonymous anonymous, p.content content, p.views views, p.likes likes, p.answer_count answer_count, p.created_at created_at, p.modified_at modified_at, u.id author_id, u.nickname author_nickname, c.id category_id, c.name category_name, t.id tag_id, t.name tag_name FROM ( SELECT * FROM posts WHERE deleted_at IS NULL AND post_type = #{postType}
AND category_id = #{filterValue}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY created_at DESC views DESC likes DESC answer_count DESC LIMIT #{offset}, #{size} ) p LEFT JOIN users u ON u.id = p.user_id LEFT JOIN category c ON c.id = p.category_id LEFT JOIN post_tags pt ON pt.post_id = p.id LEFT JOIN tags t ON t.id = pt.tag_id where true
AND u.nickname LIKE concat('%',#{searchKeyword},'%') AND p.anonymous IS FALSE

select count(p.id) from posts p left join category c on p.category_id =c.id left join users u on p.user_id = u.id where p.deleted_at is null and p.post_type = #{postType}
and p.category_id = #{filterValue}
and p.title like concat('%', #{searchKeyword}, '%')
and u.nickname like concat('%', #{searchKeyword}, '%') and p.anonymous is false

select exists(select 1
              from post_likes
              where user_id = #{authorId}
                and post_id = #{codeReviewId});

mapper.update:
update posts
set likes = likes + 1
where id = #{codeReviewId};

update posts
set views = views + 1
where id = #{codeReviewId};
